import React, { useEffect, useState } from "react";
import { onAuthStateChanged } from "firebase/auth";
import { auth } from "./firebaseConfig";
import Welcome from "./pages/Welcome";
import CreateMatch from "./components/CreateMatch";
import MatchList from "./components/MatchList";

function App() {
  const [showCreateMatch, setShowCreateMatch] = useState(false);
  const [matchList, setMatchList] = useState([]); // 投稿のリストを管理

  const toggleCreateMatch = () => {
    setShowCreateMatch((prev) => !prev);
  };
  const addMatch = (newMatch) => {
    setMatchList([...matchList, newMatch]); // 新しい投稿をリストに追加
    setShowCreateMatch(false); // 投稿後にポップアップを閉じる
  };
  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, (currentUser) => {
      setUser(currentUser);  // ユーザーのログイン状態を監視
    });
    return () => unsubscribe();
  }, []);

  return (
    <div className="App">
      {user ? (
        <>
          <CreateMatch />
          <MatchList />
        </>
      ) : (
        <Welcome />
      )}

    </div>
  );
}

export default App;
