import { useEffect, useState } from "react";
import { collection, query, orderBy, onSnapshot } from "firebase/firestore";
import { db } from "../firebaseConfig";
import "./MatchList.css";
import MatchDetailModal from "./MatchDetailModal"; // モーダルコンポーネントをインポート

const MatchList = () => {
  const [matches, setMatches] = useState([]);
  const [selectedMatch, setSelectedMatch] = useState(null); // 選択したマッチデータ

  useEffect(() => {
    const q = query(collection(db, "matches"), orderBy("timestamp", "desc"));
    const unsubscribe = onSnapshot(q, (snapshot) => {
      const matchesData = snapshot.docs.map((doc) => ({
        id: doc.id,
        ...doc.data(),
      }));
      setMatches(matchesData);
    });
    return () => unsubscribe();
  }, []);

  const openModal = (match) => {
    setSelectedMatch(match);
  };

  const closeModal = () => {
    setSelectedMatch(null);
  };

  return (
    <div className="match-list">
      {matches.map((match) => (
        <div key={match.id} className="match-card">
          <div className="match-header">
            <img src={match.avatar} alt={`${match.name}のアバター`} className="avatar" />
            <div className="match-info">
              <h3 className="match-name">{match.name}</h3>
            </div>
          </div>

          <button className="details-button" onClick={() => openModal(match)}>
            詳細を見る
          </button>
        </div>
      ))}

      {/* モーダルウィンドウを表示 */}
      {selectedMatch && (
        <MatchDetailModal match={selectedMatch} onClose={closeModal} />
      )}
    </div>
  );
};

export default MatchList;
