// src/components/MatchList.js
import { useEffect, useState } from "react";
import { collection, query, orderBy, onSnapshot } from "firebase/firestore";
import { db } from "../firebaseConfig";
import Notification from "./Notification"; // Notificationをインポート
import "./MatchList.css";  // CSSをインポート

const MatchList = () => {
  const [matches, setMatches] = useState([]);
  const [notificationVisible, setNotificationVisible] = useState(false);
  const [notificationMessage, setNotificationMessage] = useState("");

  // 投稿が成功した際に呼び出す関数
  const handlePostSuccess = (message) => {
    setNotificationMessage(message);
    setNotificationVisible(true);
  };

  useEffect(() => {
    const q = query(collection(db, "matches"), orderBy("timestamp", "desc"));
    const unsubscribe = onSnapshot(q, (snapshot) => {
      const matchesData = snapshot.docs.map((doc) => ({
        id: doc.id,
        ...doc.data(),
      }));
      setMatches(matchesData);
    });
    return () => unsubscribe();
  }, []);

  // ここに新しい投稿を追加する関数を追加
  const addNewMatch = (newMatch) => {
    // 新しいマッチをFirebaseに追加する処理
    // ここで成功時にhandlePostSuccessを呼び出す
    handlePostSuccess("投稿が完了しました!");
  };

  return (
    <div className="match-list">
      <h2>募集一覧</h2>
      {matches.map((match) => (
        <div key={match.id} className="match-card">
          <div className="match-header">
            <h3 className="match-name">{match.name}</h3>
            <p className="match-opponent">対戦相手: {match.opponent}</p>
          </div>
          <div className="match-details">
          <h2>{match.name}</h2> {/* 名前を表示 */}
            <p><strong>身長:</strong> {match.height} cm</p>
            <p><strong>体重:</strong> {match.weight} kg</p>
            <p><strong>場所:</strong> {match.location}</p>
            <p><strong>ルール:</strong> {match.rules}</p>
          </div>
        </div>
      ))}

      {/* ポップアップ通知 */}
      <Notification 
        message={notificationMessage} 
        visible={notificationVisible} 
        onClose={() => setNotificationVisible(false)} 
      />
    </div>
  );
};

export default MatchList;
