import { useState } from "react";
import CreateMatch from "./components/CreateMatch";
import MatchList from "./components/MatchList";

function App() {
  const [showCreateMatch, setShowCreateMatch] = useState(false);
  const [matches, setMatches] = useState([]); // 投稿内容を保持する状態

  const toggleCreateMatch = () => {
    setShowCreateMatch((prev) => !prev);
  };

  // 新しい募集を追加する関数
  const addMatch = (newMatch) => {
    setMatches((prevMatches) => [...prevMatches, newMatch]);
    setShowCreateMatch(false); // ポップアップを閉じる
  };

  return (
    <div className="App">
      {/* 募集一覧 */}
      <MatchList matches={matches} />

      {/* ポップアップ表示ボタン */}
      <button className="create-match-button" onClick={toggleCreateMatch}>+</button>

      {/* showCreateMatchがtrueのときのみCreateMatchをポップアップ表示 */}
      {showCreateMatch && <CreateMatch onClose={toggleCreateMatch} onAddMatch={addMatch} />}
    </div>
  );
}

export default App;
