// src/components/CreateMatch.js
import React, { useState } from "react";
import "./CreateMatch.css";

const CreateMatch = ({ onClose, onSubmit }) => {
  const [height, setHeight] = useState("");
  const [weight, setWeight] = useState("");
  const [location, setLocation] = useState("");
  const [record, setRecord] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    // 新しい投稿内容をonSubmit関数で送信
    onSubmit({ height, weight, location, record });
    // フォームをクリア
    setHeight("");
    setWeight("");
    setLocation("");
    setRecord("");
  };

  return (
    <>
      <div className="create-match-overlay" onClick={onClose} />
      <div className="create-match-modal">
        <button className="close-button" onClick={onClose}>✖️</button>
        <h2>募集を作成</h2>
        <form onSubmit={handleSubmit}>
          <div>
            <label>身長</label>
            <input
              type="number"
              value={height}
              onChange={(e) => setHeight(e.target.value)}
              placeholder="cm"
            />
          </div>
          <div>
            <label>体重</label>
            <input
              type="number"
              value={weight}
              onChange={(e) => setWeight(e.target.value)}
              placeholder="kg"
            />
          </div>
          <div>
            <label>場所</label>
            <input
              type="text"
              value={location}
              onChange={(e) => setLocation(e.target.value)}
              placeholder="市区町村"
            />
          </div>
          <div>
            <label>戦績</label>
            <input
              type="text"
              value={record}
              onChange={(e) => setRecord(e.target.value)}
              placeholder="戦績"
            />
          </div>
          <button type="submit">投稿</button>
        </form>
      </div>
    </>
  );
};

export default CreateMatch;
