// src/components/CreateMatch.js
import React from "react";
import "./CreateMatch.css";

const CreateMatch = ({ onClose }) => {
  return (
    <>
      {/* オーバーレイ */}
      <div className="create-match-overlay" onClick={onClose} />

      {/* ポップアップのメインコンテンツ */}
      <div className="create-match-modal">
        <button className="close-button" onClick={onClose}>✖️</button>
        <h2>募集を作成</h2>
        <form>
          {/* 募集内容の入力フォーム */}
          <div>
            <label>対戦相手の募集内容</label>
            <input type="text" placeholder="募集内容を入力" />
          </div>
          <div>
            <label>身長</label>
            <input type="number" placeholder="cm" />
          </div>
          <div>
            <label>体重</label>
            <input type="number" placeholder="kg" />
          </div>
          <button type="submit">投稿</button>
        </form>
      </div>
    </>
  );
};

export default CreateMatch;
